chat_template:
{%- for m in messages %}
    {%- if m.role == 'system' %}
        {{- '<|system|>' + m.content + '<|endofsystem|>\n' }}
    {%- elif m.role == 'user' %}
        {{- '<|user|>' + m.content + '<|endofuser|>' }}
    {%- elif m.role == 'assistant' %}
        {{- '<|assistant|>' + m.content }}
        {%- if not loop.last %}
            {{- '<|endofassistant|>' }}
        {%- endif %}
    {%- endif %}
{%- endfor %}
{%- if messages[-1].role != 'assistant' %}
    {{- '<|assistant|>' }}
{%- endif %}
